// SPDX-License-Identifier: UNLICENSED

pragma solidity ^0.8.23;

import "@openzeppelin/contracts/utils/Strings.sol";

import "hardhat/console.sol";

/**
 * Dynamic memory array implementation for AssertResult to facilitate unit test
 * implementation.
 */
library List {
    struct ARList {
        Assert.AssertResult[] elements;
        uint num;
    }

    function create(
        ARList memory _aList
    ) internal pure returns (ARList memory newList) {
        _aList.elements = new Assert.AssertResult[](4);
        _aList.num = 0;
        return _aList;
    }

    function _resizeUp(
        ARList memory _aList
    ) internal pure returns (ARList memory) {
        ARList memory newList;
        newList.elements = new Assert.AssertResult[](
            (_aList.elements.length * 3) / 2
        );
        for (uint i = 0; i < _aList.elements.length; i++) {
            newList.elements[i] = _aList.elements[i];
        }
        newList.num = _aList.num;
        return newList;
    }

    // Follow Solidity .pop() expectation to NOT return a value
    function pop(ARList memory _aList) internal pure returns (ARList memory) {
        _aList.num--;
        delete _aList.elements[_aList.num];
        return _aList;
    }

    function push(
        ARList memory _aList,
        Assert.AssertResult memory _result
    ) internal pure returns (ARList memory) {
        if (_aList.num == _aList.elements.length) {
            _aList = _resizeUp(_aList);
        }
        _aList.elements[_aList.num] = _result;
        _aList.num++;
        return _aList;
    }
}

contract Assert {
    struct AssertResult {
        bool passed;
        string assertionError;
        string returnedAsString;
        string expectedAsString;
        string methodName;
    }

    function _arrToString(
        uint[] memory arr
    ) internal pure returns (string memory) {
        string memory result = "[";

        for (uint i = 0; i < arr.length; i++) {
            if (i < arr.length - 1) {
                result = string.concat(result, Strings.toString(arr[i]), ", ");
            } else {
                result = string.concat(result, Strings.toString(arr[i]), "]");
            }
        }

        return result;
    }

    function _arrToString(
        address[] memory arr
    ) internal pure returns (string memory) {
        string memory result = "[";

        for (uint i = 0; i < arr.length; i++) {
            if (i < arr.length - 1) {
                result = string.concat(
                    result,
                    Strings.toHexString(uint160(arr[i]), 20),
                    ", "
                );
            } else {
                result = string.concat(
                    result,
                    Strings.toHexString(uint160(arr[i]), 20),
                    "]"
                );
            }
        }

        return result;
    }

    function _arrToString(
        string[] memory arr
    ) internal pure returns (string memory) {
        string memory result = "[";

        for (uint i = 0; i < arr.length; i++) {
            if (i < arr.length - 1) {
                result = string.concat(result, arr[i], ", ");
            } else {
                result = string.concat(result, arr[i], "]");
            }
        }

        return result;
    }

    function isTrue(bool a) public pure returns (AssertResult memory result) {
        result.passed = a;
        if (!result.passed) {
            result.assertionError = "AssertionError: result is not true";
        }
        result.returnedAsString = a == true ? "true" : "false";
        result.expectedAsString = "";
        result.methodName = "isTrue";
    }

    function isFalse(bool a) public pure returns (AssertResult memory result) {
        result.passed = !a;
        if (!result.passed) {
            result.assertionError = "AssertionError: result is not false";
        }
        result.returnedAsString = a == true ? "true" : "false";
        result.expectedAsString = "";
        result.methodName = "isFalse";
    }

    function equal(
        uint256 a,
        uint256 b
    ) public pure returns (AssertResult memory result) {
        result.passed = (a == b);
        if (!result.passed) {
            result.assertionError = string.concat(
                "AssertionError: ",
                Strings.toString(a),
                " is not equal to ",
                Strings.toString(b)
            );
        }
        result.returnedAsString = Strings.toString(a);
        result.expectedAsString = Strings.toString(b);
        result.methodName = "equal";
    }

    // TODO

    // function equal(int256 a, int256 b) public returns (AssertResult memory result) {

    // }

    // function equal(bool a, bool b) public returns (AssertResult memory result) {

    // }

    function equal(
        address a,
        address b
    ) public pure returns (AssertResult memory result) {
        result.passed = (a == b);
        string memory aString = Strings.toHexString(uint160(a), 20);
        string memory bString = Strings.toHexString(uint160(b), 20);
        if (!result.passed) {
            result.assertionError = string.concat(
                "AssertionError: ",
                aString,
                " is not equal to ",
                bString
            );
        }
        result.returnedAsString = aString;
        result.expectedAsString = bString;
        result.methodName = "equal";
    }

    // function equal(bytes32 a, bytes32 b) public returns (AssertResult memory result) {

    // }

    function equal(
        bytes memory a,
        bytes memory b
    ) public pure returns (AssertResult memory result) {
        result.passed = keccak256(a) == keccak256(b);
        if (!result.passed) {
            result.assertionError = string.concat(
                "AssertionError: ",
                string(a),
                " is not equal to ",
                string(b)
            );
        }
        result.returnedAsString = string(a);
        result.expectedAsString = string(b);
        result.methodName = "equal";
    }

    function equal(
        string memory a,
        string memory b
    ) public pure returns (AssertResult memory result) {
        result.passed = keccak256(bytes(a)) == keccak256(bytes(b));
        if (!result.passed) {
            result.assertionError = string.concat(
                "AssertionError: ",
                a,
                " is not equal to ",
                b
            );
        }
        result.returnedAsString = a;
        result.expectedAsString = b;
        result.methodName = "equal";
    }

    function equal(
        uint[] memory a,
        uint[] memory b
    ) public pure returns (AssertResult memory result) {
        result.passed = keccak256(abi.encode(a)) == keccak256(abi.encode(b));
        if (!result.passed) {
            result.assertionError = string.concat(
                "AssertionError: ",
                _arrToString(a),
                " is not equal to ",
                _arrToString(b)
            );
        }
        result.returnedAsString = _arrToString(a);
        result.expectedAsString = _arrToString(b);
        result.methodName = "equal";
    }

    function equal(
        address[] memory a,
        address[] memory b
    ) public pure returns (AssertResult memory result) {
        result.passed = keccak256(abi.encode(a)) == keccak256(abi.encode(b));
        if (!result.passed) {
            result.assertionError = string.concat(
                "AssertionError: ",
                _arrToString(a),
                " is not equal to ",
                _arrToString(b)
            );
        }
        result.returnedAsString = _arrToString(a);
        result.expectedAsString = _arrToString(b);
        result.methodName = "equal";
    }

    function equal(
        string[] memory a,
        string[] memory b
    ) public pure returns (AssertResult memory result) {
        result.passed = keccak256(abi.encode(a)) == keccak256(abi.encode(b));
        if (!result.passed) {
            result.assertionError = string.concat(
                "AssertionError: ",
                _arrToString(a),
                " is not equal to ",
                _arrToString(b)
            );
        }
        result.returnedAsString = _arrToString(a);
        result.expectedAsString = _arrToString(b);
        result.methodName = "equal";
    }

    // function notEqual(uint256 a, uint256 b) public returns (AssertResult memory result) {

    // }

    // function notEqual(int256 a, int256 b) public returns (AssertResult memory result) {

    // }

    // function notEqual(bool a, bool b) public returns (AssertResult memory result) {

    // }

    function notEqual(
        address a,
        address b
    ) public pure returns (AssertResult memory result) {
        result.passed = (a != b);
        string memory aString = Strings.toHexString(uint160(a), 20);
        if (!result.passed) {
            result.assertionError = string.concat(
                "AssertionError: ",
                " Both values are",
                aString
            );
        }
        result.returnedAsString = aString;
        result.expectedAsString = "";
        result.methodName = "equal";
    }

    // function notEqual(bytes32 a, bytes32 b) public returns (AssertResult memory result) {

    // }

    // function notEqual(string memory a, string memory b) public returns (AssertResult memory result) {

    // }

    // /*----------------- Greater than --------------------*/
    // function greaterThan(uint256 a, uint256 b) public returns (AssertResult memory result) {

    // }
    // function greaterThan(int256 a, int256 b) public returns (AssertResult memory result) {

    // }

    // function greaterThan(uint256 a, int256 b) public returns (AssertResult memory result) {

    //   }
    // }
    // function greaterThan(int256 a, uint256 b) public returns (AssertResult memory result) {

    // }
    // /*----------------- Less than --------------------*/
    // function lessThan(uint256 a, uint256 b) public returns (AssertResult memory result) {

    // }
    // function lessThan(int256 a, int256 b) public returns (AssertResult memory result) {

    // }

    // function lessThan(uint256 a, int256 b) public returns (AssertResult memory result) {

    // }
    // function lessThan(int256 a, uint256 b) public returns (AssertResult memory result) {

    // }
}

contracts/BasicMathUnitTest.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import "hardhat/console.sol";

import "./Cafe.sol";
import "./Assert.sol";

using List for List.ARList;

interface IBasicContractTest {
    function adder(
        uint _a,
        uint _b
    ) external returns (uint result, bool success);

    function subtractor(
        uint _a,
        uint _b
    ) external returns (uint result, bool success);
}

library Caller {
    function callRemoteAdder(
        IBasicContractTest _submission,
        uint _a,
        uint _b
    ) internal returns (uint, bool, bool) {
        try _submission.adder(_a, _b) returns (uint result, bool success) {
            return (result, success, false);
        } catch {
            return (0, false, true);
        }
    }

    function callRemoteSubtractor(
        IBasicContractTest _submission,
        uint _a,
        uint _b
    ) internal returns (uint, bool, bool) {
        try _submission.subtractor(_a, _b) returns (uint result, bool success) {
            return (result, success, false);
        } catch {
            return (0, false, true);
        }
    }
}

contract test1Plus2 is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        IBasicContractTest submission = IBasicContractTest(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "1 + 2 should equal 3 and NOT return an error";
        (uint res, bool err, bool callError) = Caller.callRemoteAdder(
            submission,
            1,
            2
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to adder failed", "", "", "")
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 3));
            testResult.assertResults.push(Assert.isFalse(err));
        }
        return testResult;
    }
}

contract test1PlusMaxInt is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        IBasicContractTest submission = IBasicContractTest(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "1 + MAX_INT should equal 0 and return an error";
        (uint res, bool err, bool callError) = Caller.callRemoteAdder(
            submission,
            1,
            type(uint).max
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to adder failed", "", "", "")
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 0));
            testResult.assertResults.push(Assert.isTrue(err));
        }
        return testResult;
    }
}

contract test2Minus1 is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        IBasicContractTest submission = IBasicContractTest(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "2 - 1 should equal 1 and NOT return an error";
        (uint res, bool err, bool callError) = Caller.callRemoteSubtractor(
            submission,
            2,
            1
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to subtractor failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 1));
            testResult.assertResults.push(Assert.isFalse(err));
        }
        return testResult;
    }
}

contract test1Minus2 is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        IBasicContractTest submission = IBasicContractTest(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "1 - 2 should equal 0 and return an error";
        (uint res, bool err, bool callError) = Caller.callRemoteSubtractor(
            submission,
            1,
            2
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to subtractor failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 0));
            testResult.assertResults.push(Assert.isTrue(err));
        }
        return testResult;
    }
}

// NOTE: It might be better to centralize the token distributor
contract BasicMathUnitTest is Cafe {
    constructor() ERC721("Basic Contracts Pin", "SCDBC") {
        tests.push(new test1Plus2());
        tests.push(new test1PlusMaxInt());
        tests.push(new test2Minus1());
        tests.push(new test1Minus2());
    }
}

contracts/Cafe.sol.content:
// SPDX-License-Identifier: UNLICENSED
// Cafe Version 0.2

/**
 * Cafe is a unit test framework designed to facilitate testing of contracts
 * built against a defined specification.  It awards an NFT pin if the tests
 * are passed.
 *
 * It works with Assert.sol to create a reasonably familiar system for writing
 * tests.
 */

pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "hardhat/console.sol";

import "./Assert.sol";

interface ITest {
    function execute(
        address _submissionAddress
    ) external returns (Cafe.TestResult memory);
}

interface IDeploy {
    function deploy(
        address _submissionAddress
    ) external returns (Cafe.TestResult memory, address);
}

abstract contract Cafe is ERC721, Ownable, ReentrancyGuard, Assert {
    error SoulboundToken();
    error NotActive();

    using Counters for Counters.Counter;
    Counters.Counter public tokenIds;

    mapping(address => bool) public owners;
    mapping(address => bool) submittedContracts;

    bool active = true;
    string AllTokenURI = "";

    event TestSuiteResult(
        address submission,
        bool passed,
        TestResult[] testResults
    );

    struct TestResult {
        string message;
        List.ARList assertResults;
    }

    ITest[] tests;

    /**
     * Used by unit tests to indicate they contain a factory
     * Returns the **address** of the contract deployed by the factory
     * and the test result, to validate deployment or alert to failure
     *
     */

    IDeploy deployer;

    function testContract(address _submissionAddress) public testIsActive {
        // Declare here to +1 length if there is a deployer
        TestResult[] memory testResults;
        uint i = 0;
        address testAddress = _submissionAddress;

        if (address(deployer) != address(0)) {
            testResults = new TestResult[](tests.length + 1);
            i++;
            // Use the address returned by the deployment for remaining tests
            (testResults[0], testAddress) = deployer.deploy(_submissionAddress);
        } else {
            testResults = new TestResult[](tests.length);
        }

        for (i; i < tests.length; i++) {
            testResults[i] = tests[i].execute(testAddress);
        }

        processResults(_submissionAddress, testResults);
    }

    /**
     * Check each assert in each test to see if any failed.
     *
     * Note:  The check is here instead of setting a `passed` bool in
     * `TestResult` to reduce the amount of code in each unit test.
     */
    function checkIfAllPassed(
        TestResult[] memory _testResults
    ) public pure returns (bool) {
        for (uint i = 0; i < _testResults.length; i++) {
            for (uint k = 0; k < _testResults[i].assertResults.num; k++) {
                if (!_testResults[i].assertResults.elements[k].passed) {
                    return false;
                }
            }
        }
        return true;
    }

    function processResults(
        address _submissionAddress,
        TestResult[] memory _testResults
    ) internal nonReentrant {
        bool passed = checkIfAllPassed(_testResults);

        emit TestSuiteResult(_submissionAddress, passed, _testResults);

        /**
         * Grant a soulbound NFT pin if:
         *  - This contract address has not been submitted before
         *  - The sender does not already own one of these pins
         *  - The contract submitted passes all unit tests
         */
        if (
            !submittedContracts[_submissionAddress] &&
            !owners[msg.sender] &&
            passed
        ) {
            tokenIds.increment();
            uint newId = tokenIds.current();
            owners[msg.sender] = true;
            _safeMint(msg.sender, newId);
        }

        submittedContracts[_submissionAddress] = true;
    }

    /**
     * Disallow transfers (Soulbound NFT)
     */
    function _beforeTokenTransfer(
        address _from,
        address,
        uint,
        uint
    ) internal pure override {
        if (_from != address(0)) {
            revert SoulboundToken();
        }
    }

    function setActive(bool _setActiveTo) public onlyOwner {
        active = _setActiveTo;
    }

    function setTokenURI(string memory _tokenURI) public onlyOwner {
        AllTokenURI = _tokenURI;
    }

    function tokenURI(uint256) public view override returns (string memory) {
        return AllTokenURI;
    }

    modifier testIsActive() {
        if (!active) {
            revert NotActive();
        }
        _;
    }
}

