// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

import "hardhat/console.sol";

import "./Cafe.sol";
import "./Assert.sol";

using List for List.ARList;

interface IControlStructures {
    function fizzBuzz(uint _number) external returns (string memory);

    function doNotDisturb(uint _time) external returns (string memory);
}

library Caller {
    function callRemoteFizzBuzz(
        IControlStructures _submission,
        uint _number
    ) internal returns (string memory, bool) {
        try _submission.fizzBuzz(_number) returns (string memory result) {
            return (result, false);
        } catch {
            return ("", true);
        }
    }

    function callRemoteDoNotDisturb(
        IControlStructures _submission,
        uint _time
    ) internal returns (string memory, bool) {
        try _submission.doNotDisturb(_time) returns (string memory result) {
            return (result, false);
        } catch Error(string memory reason) {
            return (reason, true);
        } catch Panic(uint errorCode) {
            return (Strings.toString(errorCode), true);
        } catch (bytes memory lowLevelData) {
            return (string(lowLevelData), true);
        }
    }
}

contract testFizzBuzz is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        IControlStructures submission = IControlStructures(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Fizzbuzz should work with tradtional rules";
        (string memory res, bool callError) = Caller.callRemoteFizzBuzz(
            submission,
            9
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to fizzBuzz failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, "Fizz"));
        }

        (res, callError) = Caller.callRemoteFizzBuzz(submission, 10);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to fizzBuzz failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, "Buzz"));
        }

        (res, callError) = Caller.callRemoteFizzBuzz(submission, 15);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to fizzBuzz failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, "FizzBuzz"));
        }

        (res, callError) = Caller.callRemoteFizzBuzz(submission, 11);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to fizzBuzz failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, "Splat"));
        }
        return testResult;
    }
}

contract testAfterHours is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        IControlStructures submission = IControlStructures(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "AfterHours should revert and assert as described";
        (string memory res, bool callError) = Caller.callRemoteDoNotDisturb(
            submission,
            2401
        );
        if (callError) {
            testResult.assertResults.push(Assert.equal(res, "1"));
        } else {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Transaction failed to panic",
                    "",
                    "",
                    ""
                )
            );
        }

        (res, callError) = Caller.callRemoteDoNotDisturb(submission, 2201);
        if (callError) {
            testResult.assertResults.push(
                Assert.equal(
                    res,
                    string(abi.encodeWithSignature("AfterHours(uint256)", 2201))
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Transaction failed to revert",
                    "",
                    "",
                    ""
                )
            );
        }

        (res, callError) = Caller.callRemoteDoNotDisturb(submission, 801);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to doNotDisturb failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, "Morning!"));
        }

        (res, callError) = Caller.callRemoteDoNotDisturb(submission, 1450);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to doNotDisturb failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, "Afternoon!"));
        }

        (res, callError) = Caller.callRemoteDoNotDisturb(submission, 1830);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to doNotDisturb failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, "Evening!"));
        }

        return testResult;
    }
}

contract ControlStructuresUT is Cafe {
    constructor() ERC721("Control Structures Pin", "SCDCS") {
        tests.push(new testFizzBuzz());
        tests.push(new testAfterHours());
    }
}

contracts/ERC20UT.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import "hardhat/console.sol";

import "./Cafe.sol";
import "./Assert.sol";

using List for List.ARList;

interface ISubmission {
    struct Issue {
        EnumerableSet.AddressSet voters;
        string issueDesc;
        uint votesFor;
        uint votesAgainst;
        uint votesAbstain;
        uint totalVotes;
        uint quorum;
        bool passed;
        bool closed;
    }

    struct ReturnableIssue {
        address[] voters;
        string issueDesc;
        uint votesFor;
        uint votesAgainst;
        uint votesAbstain;
        uint totalVotes;
        uint quorum;
        bool passed;
        bool closed;
    }

    enum Vote {
        AGAINST,
        FOR,
        ABSTAIN
    }

    function claim() external;

    function createIssue(
        string memory _issueDesc,
        uint _quorum
    ) external returns (uint);

    function getIssue(
        uint _id
    ) external view returns (ISubmission.ReturnableIssue memory);

    function vote(uint _issueId, Vote _vote) external;

    function totalSupply() external returns (uint);

    function balanceOf(address _account) external view returns (uint);

    function transfer(address to, uint256 amount) external;
}

library Caller {
    function claim(
        ISubmission _submission
    ) internal returns (string memory, bool) {
        try _submission.claim() {
            return ("", false);
        } catch (bytes memory lowLevelData) {
            return (string(lowLevelData), true);
        }
    }

    function createIssue(
        ISubmission _submission,
        string memory _issueDesc,
        uint _quorum
    ) internal returns (string memory, bool, uint) {
        try _submission.createIssue(_issueDesc, _quorum) returns (uint index) {
            return ("", false, index);
        } catch (bytes memory lowLevelData) {
            return (string(lowLevelData), true, 0);
        }
    }

    function vote(
        ISubmission _submission,
        uint _issueId,
        ISubmission.Vote _vote
    ) internal returns (string memory, bool) {
        try _submission.vote(_issueId, _vote) {
            return ("", false);
        } catch (bytes memory lowLevelData) {
            return (string(lowLevelData), true);
        }
    }

    function getIssue(
        ISubmission _submission,
        uint _id
    )
        internal
        view
        returns (string memory, bool, ISubmission.ReturnableIssue memory)
    {
        try _submission.getIssue(_id) returns (
            ISubmission.ReturnableIssue memory issue
        ) {
            return ("", false, issue);
        } catch (bytes memory lowLevelData) {
            ISubmission.ReturnableIssue memory empty;
            return (string(lowLevelData), true, empty);
        }
    }
}

contract ERC20SecondCaller {
    function claim(
        ISubmission _submission
    ) public returns (string memory, bool) {
        return Caller.claim(_submission);
    }

    function vote(
        ISubmission _submission,
        uint _id,
        ISubmission.Vote _vote
    ) public returns (string memory, bool) {
        return Caller.vote(_submission, _id, _vote);
    }
}

contract TestTokenClaim is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "User should be able to claim exactly once";

        uint amountClaimed = submission.totalSupply();

        (string memory message, bool callError) = Caller.claim(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to claim failed", "", "", "")
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(submission.balanceOf(address(this)), 100)
            );
        }

        (message, callError) = Caller.claim(submission);
        if (!callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Expected revert with TokensClaimed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(
                    message,
                    string(abi.encodeWithSignature("TokensClaimed()"))
                )
            );
        }

        uint amountClaimedAfter = submission.totalSupply();

        testResult.assertResults.push(
            Assert.equal(amountClaimedAfter, amountClaimed + 100)
        );

        return testResult;
    }
}

contract TestSecondClaimer is ITest, Assert {
    ERC20SecondCaller secondCaller;

    constructor(address _secondCaller) {
        secondCaller = ERC20SecondCaller(_secondCaller);
    }

    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Second user should be able to claim exactly once";

        uint amountClaimed = submission.totalSupply();

        (string memory message, bool callError) = secondCaller.claim(
            submission
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to claim failed", "", "", "")
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(submission.balanceOf(address(secondCaller)), 100)
            );
        }

        (message, callError) = secondCaller.claim(submission);
        if (!callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Expected revert with TokensClaimed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(
                    message,
                    string(abi.encodeWithSignature("TokensClaimed()"))
                )
            );
        }

        uint amountClaimedAfter = submission.totalSupply();

        testResult.assertResults.push(
            Assert.equal(amountClaimedAfter, amountClaimed + 100)
        );

        return testResult;
    }
}

contract TestVoting is ITest, Assert {
    ERC20SecondCaller secondCaller;
    ERC20SecondCaller thirdCaller;
    ERC20SecondCaller fourthCaller;

    constructor(
        address _secondCaller,
        address _thirdCaller,
        address _fourthCaller
    ) {
        secondCaller = ERC20SecondCaller(_secondCaller);
        thirdCaller = ERC20SecondCaller(_thirdCaller);
        fourthCaller = ERC20SecondCaller(_fourthCaller);
    }

    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Should create and allow voting on issues";

        (string memory message, bool callError, uint issueIndex) = Caller
            .createIssue(submission, "We should all learn Fortran!", 300);
        if (!callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "createIssue did not revert with NoTokensHeld()",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(
                    message,
                    string(abi.encodeWithSignature("NoTokensHeld()"))
                )
            );
        }

        uint amountClaimed = submission.totalSupply();

        // Prior tests have claimed for secondCaller, but Caller is tied to this new test contract
        (message, callError) = Caller.claim(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to claim failed", "", "", "")
            );
        }

        (message, callError) = thirdCaller.claim(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to claim failed", "", "", "")
            );
        }

        (message, callError) = fourthCaller.claim(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to claim failed", "", "", "")
            );
        }

        submission.transfer(address(fourthCaller), 50);

        uint amountClaimedAfter = submission.totalSupply();

        testResult.assertResults.push(
            Assert.equal(amountClaimedAfter, amountClaimed + 300)
        );

        (message, callError, issueIndex) = Caller.createIssue(
            submission,
            "We should all learn Solidity!",
            400
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to createIssue failed",
                    "",
                    "",
                    ""
                )
            );
        }

        (message, callError) = Caller.vote(
            submission,
            issueIndex,
            ISubmission.Vote.FOR
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to vote failed", "", "", "")
            );
        }

        // 50 votes FOR
        (message, callError) = Caller.vote(
            submission,
            issueIndex,
            ISubmission.Vote.FOR
        );
        if (!callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "vote did not revert with AlreadyVoted()",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(
                    message,
                    string(abi.encodeWithSignature("AlreadyVoted()"))
                )
            );
        }

        // 100 votes AGAINST
        (message, callError) = secondCaller.vote(
            submission,
            issueIndex,
            ISubmission.Vote.AGAINST
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to vote failed", "", "", "")
            );
        }

        // 100 votes FOR
        (message, callError) = thirdCaller.vote(
            submission,
            issueIndex,
            ISubmission.Vote.FOR
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to vote failed", "", "", "")
            );
        }

        // 150 votes ABSTAIN
        (message, callError) = fourthCaller.vote(
            submission,
            issueIndex,
            ISubmission.Vote.ABSTAIN
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to vote failed", "", "", "")
            );
        }

        ISubmission.ReturnableIssue memory issue;

        (message, callError, issue) = Caller.getIssue(submission, issueIndex);

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to vote failed", "", "", "")
            );
        } else {
            testResult.assertResults.push(
                // Don't check array equality because order cannot be guaranteed
                Assert.equal(issue.voters.length, 4)
            );
            testResult.assertResults.push(
                Assert.equal(issue.issueDesc, "We should all learn Solidity!")
            );
            testResult.assertResults.push(Assert.equal(issue.votesFor, 150));
            testResult.assertResults.push(
                Assert.equal(issue.votesAgainst, 100)
            );
            testResult.assertResults.push(
                Assert.equal(issue.votesAbstain, 150)
            );
            testResult.assertResults.push(Assert.equal(issue.totalVotes, 400));
            testResult.assertResults.push(Assert.equal(issue.quorum, 400));
            testResult.assertResults.push(Assert.isTrue(issue.closed));
            testResult.assertResults.push(Assert.isTrue(issue.passed));
        }

        return testResult;
    }
}

contract ERC20UT is Cafe {
    constructor(
        address _secondCallerAddress,
        address _thirdCallerAddress,
        address _fourthCallerAddress
    ) ERC721("ERC20 Pin", "SCDERC20") {
        tests.push(new TestTokenClaim());
        tests.push(new TestSecondClaimer(_secondCallerAddress));
        tests.push(
            new TestVoting(
                _secondCallerAddress,
                _thirdCallerAddress,
                _fourthCallerAddress
            )
        );
    }
}

contracts/ERC721UT.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import "hardhat/console.sol";

import "./Cafe.sol";
import "./Assert.sol";

using List for List.ARList;

interface ISubmission {
    struct Haiku {
        address author;
        string line1;
        string line2;
        string line3;
    }

    function mintHaiku(
        string memory _line1,
        string memory _line2,
        string memory _line3
    ) external;

    function ownerOf(uint _id) external view returns (address);

    function counter() external view returns (uint);

    function shareHaiku(uint _id, address _to) external;

    function getMySharedHaikus() external view returns (Haiku[] memory);
}

library Caller {
    function counter(
        ISubmission _submission
    ) internal view returns (string memory, bool, uint) {
        try _submission.counter() returns (uint res) {
            return ("", false, res);
        } catch {
            return ("Call failed", true, 0);
        }
    }

    function mintHaiku(
        ISubmission _submission,
        string memory _line1,
        string memory _line2,
        string memory _line3
    ) internal returns (string memory, bool) {
        try _submission.mintHaiku(_line1, _line2, _line3) {
            return ("", false);
        } catch (bytes memory lowLevelData) {
            return (string(lowLevelData), true);
        }
    }

    function shareHaiku(
        ISubmission _submission,
        uint _id,
        address _to
    ) internal returns (string memory, bool) {
        try _submission.shareHaiku(_id, _to) {
            return ("", false);
        } catch (bytes memory lowLevelData) {
            // if (
            //     keccak256(abi.encodeWithSignature("AlreadyVoted()")) == keccak256(lowLevelData)
            // ) {
            //     console.log("Custom Error: AlreadyVoted()");
            // }
            return (string(lowLevelData), true);
        }
    }

    function getMySharedHaikus(
        ISubmission _submission
    ) internal view returns (string memory, bool, ISubmission.Haiku[] memory) {
        try _submission.getMySharedHaikus() returns (
            ISubmission.Haiku[] memory result
        ) {
            return ("", false, result);
        } catch (bytes memory lowLevelData) {
            ISubmission.Haiku[] memory empty;
            return (string(lowLevelData), true, empty);
        }
    }
}

contract ERC721SecondCaller {
    function mintHaiku(
        ISubmission _submission,
        string memory _line1,
        string memory _line2,
        string memory _line3
    ) public returns (string memory, bool) {
        return Caller.mintHaiku(_submission, _line1, _line2, _line3);
    }

    function shareHaiku(
        ISubmission _submission,
        uint _id,
        address _to
    ) public returns (string memory, bool) {
        return Caller.shareHaiku(_submission, _id, _to);
    }

    function getMySharedHaikus(
        ISubmission _submission
    ) public view returns (string memory, bool, ISubmission.Haiku[] memory) {
        return Caller.getMySharedHaikus(_submission);
    }

    // solhint-disable
    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes calldata data
    ) external returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }
    // solhint-enable
}

contract TestMintHaiku is ITest, Assert, IERC721Receiver {
    // solhint-disable
    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes calldata data
    ) external returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    // solhint-enable

    ERC721SecondCaller secondCaller;

    constructor(address _secondCaller) {
        secondCaller = ERC721SecondCaller(_secondCaller);
    }

    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult
            .message = "Should be able to mint unique haikus but block copies";

        (string memory message, bool callError) = Caller.mintHaiku(
            submission,
            "This is an attempt",
            "To create a unique poem",
            "It should pass this time"
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to mintHaiku failed",
                    "",
                    "",
                    ""
                )
            );
        }

        uint counter;
        (message, callError, counter) = Caller.counter(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to counter failed.  Is it public?",
                    "",
                    "",
                    ""
                )
            );
        } else {
            address ownerOfHaiku = submission.ownerOf(counter - 1);
            testResult.assertResults.push(
                Assert.equal(ownerOfHaiku, address(this))
            );
        }

        (message, callError) = secondCaller.mintHaiku(
            submission,
            "This is an attempt",
            "To copy anothers work",
            "It should fail this time"
        );

        if (!callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to mintHaiku did not revert",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(
                    message,
                    string(abi.encodeWithSignature("HaikuNotUnique()"))
                )
            );
        }

        return testResult;
    }
}

contract TestSharing is ITest, Assert, IERC721Receiver {
    // solhint-disable
    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes calldata data
    ) external returns (bytes4) {
        return IERC721Receiver.onERC721Received.selector;
    }

    // solhint-enable

    ERC721SecondCaller secondCaller;
    ERC721SecondCaller thirdCaller;
    ERC721SecondCaller fourthCaller;

    constructor(
        address _secondCaller,
        address _thirdCaller,
        address _fourthCaller
    ) {
        secondCaller = ERC721SecondCaller(_secondCaller);
        thirdCaller = ERC721SecondCaller(_thirdCaller);
        fourthCaller = ERC721SecondCaller(_fourthCaller);
    }

    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Should create and share access to haikus";

        (string memory message, bool callError) = Caller.mintHaiku(
            submission,
            "Another address mints",
            "More bad poetry now",
            "Will anyone care?"
        );

        uint counter;
        (message, callError, counter) = Caller.counter(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to counter failed.  Is it public?",
                    "",
                    "",
                    ""
                )
            );
        } else {
            address ownerOfHaiku = submission.ownerOf(counter - 1);
            testResult.assertResults.push(
                Assert.equal(ownerOfHaiku, address(this))
            );
        }

        (message, callError) = secondCaller.mintHaiku(
            submission,
            "Original work",
            "Now it mints an NFT",
            "Who to give it to?"
        );
        (message, callError, counter) = Caller.counter(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to counter failed.  Is it public?",
                    "",
                    "",
                    ""
                )
            );
        } else {
            address ownerOfHaiku = submission.ownerOf(counter - 1);
            testResult.assertResults.push(
                Assert.equal(ownerOfHaiku, address(secondCaller))
            );
        }

        (message, callError) = thirdCaller.mintHaiku(
            submission,
            "Copy/paste errors",
            "Make hard to debug problems",
            "Will we ever learn?"
        );

        (message, callError, counter) = Caller.counter(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to counter failed.  Is it public?",
                    "",
                    "",
                    ""
                )
            );
        } else {
            address ownerOfHaiku = submission.ownerOf(counter - 1);
            testResult.assertResults.push(
                Assert.equal(ownerOfHaiku, address(thirdCaller))
            );
        }

        ISubmission.Haiku[] memory sharedHaikus;
        (message, callError, sharedHaikus) = fourthCaller.getMySharedHaikus(
            submission
        );
        if (!callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to getMySharedHaikus did not revert",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(
                    message,
                    string(abi.encodeWithSignature("NoHaikusShared()"))
                )
            );
        }

        (message, callError, counter) = Caller.counter(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to counter failed.  Is it public?",
                    "",
                    "",
                    ""
                )
            );
        } else {
            (message, callError) = Caller.shareHaiku(
                submission,
                counter - 1,
                address(fourthCaller)
            );
            if (!callError) {
                testResult.assertResults.push(
                    Assert.AssertResult(
                        false,
                        "Call to shareHaiku should revert with NotYourHaiku",
                        "",
                        "",
                        ""
                    )
                );
            } else {
                testResult.assertResults.push(
                    Assert.equal(
                        message,
                        string(
                            abi.encodeWithSignature(
                                "NotYourHaiku(uint256)",
                                counter - 1
                            )
                        )
                    )
                );
            }

            (message, callError) = Caller.shareHaiku(
                submission,
                counter - 3,
                address(fourthCaller)
            );
            if (callError) {
                testResult.assertResults.push(
                    Assert.AssertResult(
                        false,
                        "Call to shareHaiku failed",
                        "",
                        "",
                        ""
                    )
                );
            }

            (message, callError) = secondCaller.shareHaiku(
                submission,
                counter - 2,
                address(fourthCaller)
            );
            if (callError) {
                testResult.assertResults.push(
                    Assert.AssertResult(
                        false,
                        "Call to shareHaiku failed",
                        "",
                        "",
                        ""
                    )
                );
            }

            (message, callError) = thirdCaller.shareHaiku(
                submission,
                counter - 1,
                address(fourthCaller)
            );
            if (callError) {
                testResult.assertResults.push(
                    Assert.AssertResult(
                        false,
                        "Call to shareHaiku failed",
                        "",
                        "",
                        ""
                    )
                );
            }
        }

        (message, callError, sharedHaikus) = fourthCaller.getMySharedHaikus(
            submission
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to getMySharedHaikus failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(sharedHaikus.length, 3));
            testResult.assertResults.push(
                Assert.equal(sharedHaikus[0].line1, "Another address mints")
            );
            testResult.assertResults.push(
                Assert.equal(sharedHaikus[1].line2, "Now it mints an NFT")
            );
            testResult.assertResults.push(
                Assert.equal(sharedHaikus[1].line2, "Will we ever learn?")
            );
        }

        return testResult;
    }
}

