// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import "hardhat/console.sol";

import "./Cafe.sol";
import "./Assert.sol";

using List for List.ARList;

interface ISubmission {
    function diffWithNeighbor(
        uint _a,
        uint _b,
        uint _c,
        uint _d
    ) external pure returns (uint[] memory);

    function applyModifier(
        uint _base,
        int _modifier
    ) external pure returns (uint);

    function popWithReturn() external returns (uint);

    function addToArr(uint _num) external;

    function getArr() external view returns (uint[] memory);

    function resetArr() external;
}

library Caller {
    function diffWithNeighbor(
        ISubmission _submission,
        uint _a,
        uint _b,
        uint _c,
        uint _d
    ) internal pure returns (uint[] memory, bool) {
        try _submission.diffWithNeighbor(_a, _b, _c, _d) returns (
            uint[] memory result
        ) {
            return (result, false);
        } catch {
            uint[] memory result;
            return (result, true);
        }
    }

    function applyModifier(
        ISubmission _submission,
        uint _base,
        int _modifier
    ) internal pure returns (uint, bool) {
        try _submission.applyModifier(_base, _modifier) returns (uint result) {
            return (result, false);
        } catch {
            return (0, true);
        }
    }

    function popWithReturn(
        ISubmission _submission
    ) internal returns (uint, bool) {
        try _submission.popWithReturn() returns (uint result) {
            return (result, false);
        } catch {
            return (0, true);
        }
    }
}

contract TestDiffWithNeighbor is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external pure override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Diff with neighbor should handle many values";

        (uint[] memory res, bool callError) = Caller.diffWithNeighbor(
            submission,
            1,
            2,
            3,
            4
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to diffWithNeighbor failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res[0], 1));
            testResult.assertResults.push(Assert.equal(res[1], 1));
            testResult.assertResults.push(Assert.equal(res[2], 1));
        }

        (res, callError) = Caller.diffWithNeighbor(submission, 9, 8, 6, 3);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to diffWithNeighbor failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res[0], 1));
            testResult.assertResults.push(Assert.equal(res[1], 2));
            testResult.assertResults.push(Assert.equal(res[2], 3));
        }

        (res, callError) = Caller.diffWithNeighbor(
            submission,
            1000,
            1,
            20000,
            50000
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to diffWithNeighbor failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res[0], 999));
            testResult.assertResults.push(Assert.equal(res[1], 19999));
            testResult.assertResults.push(Assert.equal(res[2], 30000));
        }

        return testResult;
    }
}

contract TestApplyModifier is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external pure override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Should handle positive and negative modifiers";

        (uint res, bool callError) = Caller.applyModifier(
            submission,
            230604,
            -10
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to applyModifier failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 230594));
        }

        (res, callError) = Caller.applyModifier(submission, 1000, -99);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to applyModifier failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 901));
        }

        (res, callError) = Caller.applyModifier(submission, 150000, 99);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to applyModifier failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 150099));
        }

        return testResult;
    }
}

contract TestPopWithReturn is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Should add and pop values from array";

        submission.resetArr();
        submission.addToArr(8);
        submission.addToArr(10);
        submission.addToArr(55);

        (uint res, bool callError) = Caller.popWithReturn(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to popWithReturn failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 55));
        }

        uint[] memory currentArr = submission.getArr();

        testResult.assertResults.push(Assert.equal(currentArr.length, 2));

        (res, callError) = Caller.popWithReturn(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to popWithReturn failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 10));
        }

        currentArr = submission.getArr();

        testResult.assertResults.push(Assert.equal(currentArr.length, 1));

        submission.addToArr(9);
        submission.addToArr(10);
        submission.addToArr(11);
        submission.addToArr(20);
        submission.addToArr(30);

        (res, callError) = Caller.popWithReturn(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to popWithReturn failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 30));
        }

        (res, callError) = Caller.popWithReturn(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to popWithReturn failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 20));
        }

        currentArr = submission.getArr();

        uint[4] memory expectedRaw = [uint(8), 9, 10, 11];
        uint[] memory expectedArr = new uint[](4);
        for (uint i = 0; i < 4; i++) {
            expectedArr[i] = expectedRaw[i];
        }

        testResult.assertResults.push(Assert.equal(currentArr, expectedArr));

        return testResult;
    }
}

contract ErrorTriageUT is Cafe {
    constructor() ERC721("Error Triage Pin", "SCDET") {
        tests.push(new TestDiffWithNeighbor());
        tests.push(new TestApplyModifier());
        tests.push(new TestPopWithReturn());
    }
}

