// SPDX-License-Identifier: MIT

pragma solidity ^0.8.23;

// import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

contract HaikuNFT is ERC721 {
    uint public counter = 1;

    struct Haiku {
        address author;
        string line1;
        string line2;
        string line3;
    }

    Haiku[] public haikus;
    mapping(address => uint[]) public sharedHaikus;
    mapping(string => bool) public lineUsed;

    error NotYourHaiku(uint);
    error NoHaikusShared();
    error HaikuNotUnique();

    constructor(
        string memory _name,
        string memory _symbol
    ) ERC721(_name, _symbol) {
        haikus.push();
    }

    function mintHaiku(
        string memory _line1,
        string memory _line2,
        string memory _line3
    ) external {
        if (lineUsed[_line1] || lineUsed[_line2] || lineUsed[_line3]) {
            revert HaikuNotUnique();
        }

        lineUsed[_line1] = true;
        lineUsed[_line2] = true;
        lineUsed[_line3] = true;

        haikus.push(Haiku(msg.sender, _line1, _line2, _line3));
        _safeMint(msg.sender, counter);
        counter++;
    }

    function shareHaiku(uint _id, address _to) public {
        if (msg.sender != ownerOf(_id)) {
            revert NotYourHaiku(_id);
        }

        sharedHaikus[_to].push(_id);
    }

    function getMySharedHaikus() public view returns (Haiku[] memory) {
        uint[] storage ids = sharedHaikus[msg.sender];

        if (ids.length == 0) {
            revert NoHaikusShared();
        }

        Haiku[] memory myHaikus = new Haiku[](ids.length);

        for (uint i = 0; i < myHaikus.length; i++) {
            myHaikus[i] = haikus[ids[i]];
        }

        return myHaikus;
    }
}

