    function _addApprovedRecord(string memory _name) private {
        approvedRecords[_name] = true;
        listOfApproved.push(_name);
        numApprovedRecords++;
    }

    function getApprovedRecords() public view returns (string[] memory) {
        return listOfApproved;
    }

