// SPDX-License-Identifier: MIT

pragma solidity ^0.8.23;

// Imports formatted for Remix
// import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/Ownable.sol";
// import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/utils/structs/EnumerableSet.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

contract AddressBook is Ownable {
    using EnumerableSet for EnumerableSet.UintSet;

    struct Contact {
        uint id;
        string firstName;
        string lastName;
        uint[] phoneNumbers;
    }

    mapping(uint => Contact) public contacts;
    EnumerableSet.UintSet private contactIds;
    uint currentId = 0;

    error ContactNotFound(uint id);

    constructor(address _owner) {
        transferOwnership(_owner);
    }

    function addContact(
        string memory _firstName,
        string memory _lastName,
        uint[] memory _phoneNumbers
    ) public onlyOwner {
        contacts[currentId] = Contact(
            currentId,
            _firstName,
            _lastName,
            _phoneNumbers
        );
        contactIds.add(currentId);
        currentId++;
    }

    function deleteContact(uint _id) public onlyOwner {
        if (!contactIds.contains(_id)) {
            revert ContactNotFound(_id);
        }
        delete contacts[_id];
        contactIds.remove(_id);
    }

    function getContact(uint _id) public view returns (Contact memory) {
        return contacts[_id];
    }

    function getAllContacts() public view returns (Contact[] memory) {
        uint[] memory contactIdList = contactIds.values();
        uint numContacts = contactIdList.length;
        Contact[] memory allContacts = new Contact[](numContacts);

        for (uint i = 0; i < numContacts; i++) {
            allContacts[i] = contacts[contactIdList[i]];
        }

        return allContacts;
    }
}

contract AddressBookFactory {
    function deploy() public returns (address) {
        AddressBook deployed = new AddressBook(msg.sender);
        return address(deployed);
    }
}

