// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import "hardhat/console.sol";

import "./Cafe.sol";
import "./Assert.sol";

using List for List.ARList;

interface ISubmission {
    struct Haiku {
        string line1;
        string line2;
        string line3;
    }

    function getHaiku() external view returns (Haiku memory);

    function saveHaiku(
        string memory _line1,
        string memory _line2,
        string memory _line3
    ) external;

    function shruggieHaiku() external view returns (Haiku memory);
}

library Caller {
    function saveHaiku(
        ISubmission _submission,
        string memory _line1,
        string memory _line2,
        string memory _line3
    ) internal returns (bool) {
        try _submission.saveHaiku(_line1, _line2, _line3) {
            return false;
        } catch {
            return true;
        }
    }

    function getHaiku(
        ISubmission _submission
    ) internal view returns (ISubmission.Haiku memory, bool) {
        try _submission.getHaiku() returns (ISubmission.Haiku memory result) {
            return (result, false);
        } catch {
            ISubmission.Haiku memory result;
            return (result, true);
        }
    }

    function shruggieHaiku(
        ISubmission _submission
    ) internal view returns (ISubmission.Haiku memory, bool) {
        try _submission.shruggieHaiku() returns (
            ISubmission.Haiku memory result
        ) {
            return (result, false);
        } catch {
            ISubmission.Haiku memory result;
            return (result, true);
        }
    }
}

contract TestShruggieHaiku is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Should be able to save haiku and get shruggie";

        bool callError = Caller.saveHaiku(
            submission,
            "I don't have the time",
            "To write a good haiku here",
            "So much more to teach!"
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to saveHaiku failed",
                    "",
                    "",
                    ""
                )
            );
        }

        ISubmission.Haiku memory res;
        (res, callError) = Caller.shruggieHaiku(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to haiku failed", "", "", "")
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(res.line1, "I don't have the time")
            );
            testResult.assertResults.push(
                Assert.equal(res.line2, "To write a good haiku here")
            );
            testResult.assertResults.push(
                Assert.equal(res.line3, unicode"So much more to teach! ")
            );
        }

        // Validate that original haiku is correct and unchanged
        (res, callError) = Caller.getHaiku(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to haiku failed", "", "", "")
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(res.line1, "I don't have the time")
            );
            testResult.assertResults.push(
                Assert.equal(res.line2, "To write a good haiku here")
            );
            testResult.assertResults.push(
                Assert.equal(res.line3, "So much more to teach!")
            );
        }

        return testResult;
    }
}

contract ImportsUT is Cafe {
    constructor() ERC721("Imports Pin", "SCDI") {
        tests.push(new TestShruggieHaiku());
    }
}

