// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import "hardhat/console.sol";

import "./Cafe.sol";
import "./Assert.sol";

using List for List.ARList;

interface ISubmission {
    function getApprovedRecords() external view returns (string[] memory);

    function addRecord(string memory _albumName) external;

    function getUserFavorites(
        address _address
    ) external view returns (string[] memory);

    function resetUserFavorites() external;
}

library Caller {
    function getApprovedRecords(
        ISubmission _submission
    ) internal view returns (string[] memory, bool) {
        try _submission.getApprovedRecords() returns (string[] memory result) {
            return (result, false);
        } catch {
            string[] memory badResult;
            return (badResult, true);
        }
    }

    function addRecord(
        ISubmission _submission,
        string memory _name
    ) internal returns (bool) {
        try _submission.addRecord(_name) {
            return (false);
        } catch {
            return (true);
        }
    }

    function getUserFavorites(
        ISubmission _submission,
        address _address
    ) internal view returns (string[] memory, bool) {
        try _submission.getUserFavorites(_address) returns (
            string[] memory result
        ) {
            return (result, false);
        } catch {
            string[] memory badResult;
            return (badResult, true);
        }
    }

    function resetUserFavorites(
        ISubmission _submission
    ) internal returns (bool) {
        try _submission.resetUserFavorites() {
            return (false);
        } catch {
            return (true);
        }
    }
}

// Create a second contract to have a second address to test with
contract MappingsSecondCaller {
    function secondAddRecord(
        ISubmission _submission,
        string memory _albumName
    ) public returns (bool) {
        return Caller.addRecord(_submission, _albumName);
    }

    function secondResetUserFavorites(ISubmission _submission) public {
        Caller.resetUserFavorites(_submission);
    }
}

contract TestGetApprovedRecords is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external view override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Should return the correct list";

        (string[] memory result, bool callError) = Caller.getApprovedRecords(
            submission
        );

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to getApprovedRecords failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            string[9] memory expectedAlbums = [
                "Thriller",
                "Back in Black",
                "The Bodyguard",
                "The Dark Side of the Moon",
                "Their Greatest Hits (1971-1975)",
                "Hotel California",
                "Come On Over",
                "Rumours",
                "Saturday Night Fever"
            ];
            string[] memory expected = new string[](9);
            for (uint i = 0; i < expected.length; i++) {
                expected[i] = expectedAlbums[i];
            }
            testResult.assertResults.push(Assert.equal(result, expected));
        }
        return testResult;
    }
}

contract TestGetUserFavorites is ITest, Assert {
    MappingsSecondCaller secondCaller;

    constructor(address _secondCallerAddress) {
        secondCaller = MappingsSecondCaller(_secondCallerAddress);
    }

    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Should save and be able to get favorites";

        submission.resetUserFavorites();
        secondCaller.secondResetUserFavorites(submission);

        bool callError = Caller.addRecord(submission, "Thriller");
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to addRecord failed",
                    "",
                    "",
                    ""
                )
            );
        }

        callError = Caller.addRecord(submission, "Back in Black");
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to addRecord failed",
                    "",
                    "",
                    ""
                )
            );
        }

        (string[] memory albums, bool err) = Caller.getUserFavorites(
            submission,
            address(this)
        );

        if (err) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to getUserFavorites failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            string[] memory expectedAlbums = new string[](2);
            expectedAlbums[0] = "Thriller";
            expectedAlbums[1] = "Back in Black";

            testResult.assertResults.push(Assert.equal(albums, expectedAlbums));
        }

        // Use secondCaller to get different msg.sender
        callError = secondCaller.secondAddRecord(submission, "Rumours");
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to addRecord failed",
                    "",
                    "",
                    ""
                )
            );
        }

        callError = secondCaller.secondAddRecord(
            submission,
            "Saturday Night Fever"
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to addRecord failed",
                    "",
                    "",
                    ""
                )
            );
        }

        (albums, err) = Caller.getUserFavorites(
            submission,
            address(secondCaller)
        );

        if (err) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to getUserFavorites failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            string[] memory expectedAlbums = new string[](2);
            expectedAlbums[0] = "Rumours";
            expectedAlbums[1] = "Saturday Night Fever";

            testResult.assertResults.push(Assert.equal(albums, expectedAlbums));
        }

        return testResult;
    }
}

contract MappingsUT is Cafe {
    constructor(address _secondCallerAddress) ERC721("Mappings Pin", "SCDM") {
        tests.push(new TestGetApprovedRecords());
        tests.push(new TestGetUserFavorites(_secondCallerAddress));
    }
}

