// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import "hardhat/console.sol";

import "./Cafe.sol";
import "./Assert.sol";

using List for List.ARList;

address constant CAN_RECEIVE = 0x83c4aFD6E1400B78c9ac9b9dc9a5ceeD726A6D36;

contract ZeroBalance {}

interface ISubmission {
    function claim() external;

    function safeTransfer(address _to, uint _amount) external;

    function balances(address _address) external returns (uint);

    function totalSupply() external returns (uint);

    function totalClaimed() external returns (uint);
}

library Caller {
    function claim(
        ISubmission _submission
    ) internal returns (string memory, bool) {
        try _submission.claim() {
            return ("", false);
        } catch (bytes memory lowLevelData) {
            return (string(lowLevelData), true);
        }
    }

    function safeTransfer(
        ISubmission _submission,
        address _to,
        uint _amount
    ) internal returns (string memory, bool) {
        try _submission.safeTransfer(_to, _amount) {
            return ("", false);
        } catch (bytes memory lowLevelData) {
            return (string(lowLevelData), true);
        }
    }
}

contract MinimalTokenSecondCaller {
    function claim(
        ISubmission submission
    ) public returns (string memory, bool) {
        return Caller.claim(submission);
    }
}

contract TestTokenClaim is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "User should be able to claim exactly once";

        uint amountClaimed = submission.totalClaimed();

        (string memory message, bool callError) = Caller.claim(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to claim failed", "", "", "")
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(submission.balances(address(this)), 1000)
            );
        }

        (message, callError) = Caller.claim(submission);
        if (!callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Expected revert with TokensClaimed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(
                    message,
                    string(abi.encodeWithSignature("TokensClaimed()"))
                )
            );
        }

        uint amountClaimedAfter = submission.totalClaimed();

        testResult.assertResults.push(
            Assert.equal(amountClaimedAfter, amountClaimed + 1000)
        );

        return testResult;
    }
}

contract TestSecondClaimer is ITest, Assert {
    MinimalTokenSecondCaller secondCaller;

    constructor(address _secondCaller) {
        secondCaller = MinimalTokenSecondCaller(_secondCaller);
    }

    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Second user should be able to claim exactly once";

        uint amountClaimed = submission.totalClaimed();

        (string memory message, bool callError) = secondCaller.claim(
            submission
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to claim failed", "", "", "")
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(submission.balances(address(secondCaller)), 1000)
            );
        }

        (message, callError) = secondCaller.claim(submission);
        if (!callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Expected revert with TokensClaimed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(
                    message,
                    string(abi.encodeWithSignature("TokensClaimed()"))
                )
            );
        }

        uint amountClaimedAfter = submission.totalClaimed();

        testResult.assertResults.push(
            Assert.equal(amountClaimedAfter, amountClaimed + 1000)
        );

        return testResult;
    }
}

contract TestSafeTransfer is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Should only transfer to funded addresses";

        // Claim tokens to use for trading
        (string memory message, bool callError) = Caller.claim(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to claim failed", "", "", "")
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(submission.balances(address(this)), 1000)
            );
        }

        // Deploy an empty contract to have a guaranteed zero balance
        ZeroBalance zeroBalance = new ZeroBalance();

        (message, callError) = Caller.safeTransfer(
            submission,
            CAN_RECEIVE,
            100
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to safeTransfer failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(submission.balances(CAN_RECEIVE), 100)
            );
        }

        (message, callError) = Caller.safeTransfer(
            submission,
            address(zeroBalance),
            100
        );
        if (!callError) {
            console.log("didn't revert");
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Expected UnsafeTransfer revert",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(
                    message,
                    string(
                        abi.encodeWithSignature(
                            "UnsafeTransfer(address)",
                            address(zeroBalance)
                        )
                    )
                )
            );
        }

        (message, callError) = Caller.safeTransfer(submission, address(0), 100);
        if (!callError) {
            console.log("didn't revert");
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Expected UnsafeTransfer revert",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(
                    message,
                    string(
                        abi.encodeWithSignature(
                            "UnsafeTransfer(address)",
                            address(0)
                        )
                    )
                )
            );
        }

        return testResult;
    }
}

contract MinimalTokenUT is Cafe {
    constructor(
        address _secondCallerAddress
    ) ERC721("Minimal Token Pin", "SCDMT") {
        tests.push(new TestTokenClaim());
        tests.push(new TestSecondClaimer(_secondCallerAddress));
        tests.push(new TestSafeTransfer());
    }
}

contracts/NewUT.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import "hardhat/console.sol";

import "./Cafe.sol";
import "./Assert.sol";

using List for List.ARList;

interface ISubmission {
    function deploy() external returns (address);
}

interface IAddressBook {
    struct Contact {
        uint id;
        string firstName;
        string lastName;
        uint[] phoneNumbers;
    }

    function addContact(
        string memory _firstName,
        string memory _lastName,
        uint[] memory _phoneNumbers
    ) external;

    function deleteContact(uint _id) external;

    function getContact(uint _id) external view returns (Contact memory);

    function getAllContacts() external view returns (Contact[] memory);
}

library Caller {
    function deploy(ISubmission _submission) internal returns (address, bool) {
        try _submission.deploy() returns (address result) {
            return (result, false);
        } catch {
            return (address(0), true);
        }
    }

    function addContact(
        IAddressBook _addressBook,
        string memory _firstName,
        string memory _lastName,
        uint[] memory _phoneNumbers
    ) internal returns (string memory, bool) {
        try _addressBook.addContact(_firstName, _lastName, _phoneNumbers) {
            return ("", false);
        } catch Error(string memory reason) {
            return (reason, true);
        } catch {
            return ("", true);
        }
    }

    function deleteContact(
        IAddressBook _addressBook,
        uint _id
    ) internal returns (string memory, bool) {
        try _addressBook.deleteContact(_id) {
            return ("", false);
        } catch Error(string memory reason) {
            return (reason, true);
        } catch {
            return ("", true);
        }
    }

    function getContact(
        IAddressBook _addressBook,
        uint _id
    ) internal view returns (IAddressBook.Contact memory, bool) {
        try _addressBook.getContact(_id) returns (
            IAddressBook.Contact memory result
        ) {
            return (result, false);
        } catch {
            IAddressBook.Contact memory result;
            return (result, true);
        }
    }

    function getAllContacts(
        IAddressBook _addressBook
    ) internal view returns (IAddressBook.Contact[] memory, bool) {
        try _addressBook.getAllContacts() returns (
            IAddressBook.Contact[] memory result
        ) {
            return (result, false);
        } catch {
            IAddressBook.Contact[] memory result;
            return (result, true);
        }
    }
}

contract NewSecondCaller {
    function addContact(
        IAddressBook _addressBook,
        string memory _firstName,
        string memory _lastName,
        uint[] memory _phoneNumbers
    ) public returns (string memory, bool) {
        return
            Caller.addContact(
                _addressBook,
                _firstName,
                _lastName,
                _phoneNumbers
            );
    }

    function deleteContact(
        IAddressBook _addressBook,
        uint _id
    ) public returns (string memory, bool) {
        return Caller.deleteContact(_addressBook, _id);
    }
}

contract TestDeployment is IDeploy, Assert {
    function deploy(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory, address) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Factory can deploy contract and return address";

        // Use the factory to deploy an instance of addressbook owned by
        // the UT contract
        (address deployedAddress, bool callError) = Caller.deploy(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to deploy failed", "", "", "")
            );
        } else {
            testResult.assertResults.push(
                Assert.notEqual(deployedAddress, address(0))
            );
        }

        return (testResult, deployedAddress);
    }
}

contract TestAddDelete is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        IAddressBook deployed = IAddressBook(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Owner should be able to add and delete";

        uint[] memory phoneNumbers = new uint[](1);
        phoneNumbers[0] = 2021234567;
        (string memory message, bool callError) = Caller.addContact(
            deployed,
            "Brian",
            "Doyle",
            phoneNumbers
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to addContact failed",
                    "",
                    "",
                    ""
                )
            );
        }

        phoneNumbers[0] = 2021234555;
        (message, callError) = Caller.addContact(
            deployed,
            "Joe",
            "Smith",
            phoneNumbers
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to addContact failed",
                    "",
                    "",
                    ""
                )
            );
        }

        phoneNumbers[0] = 2021234571;
        (message, callError) = Caller.addContact(
            deployed,
            "Jane",
            "Doe",
            phoneNumbers
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to addContact failed",
                    "",
                    "",
                    ""
                )
            );
        }

        IAddressBook.Contact memory contact;
        (contact, callError) = Caller.getContact(deployed, 0);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to getContact failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(contact.id, 0));
            testResult.assertResults.push(
                Assert.equal(contact.lastName, "Doyle")
            );
        }

        (contact, callError) = Caller.getContact(deployed, 2);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to getContact failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(contact.firstName, "Jane")
            );
            testResult.assertResults.push(
                Assert.equal(contact.phoneNumbers[0], 2021234571)
            );
        }

        (message, callError) = Caller.deleteContact(deployed, 1);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to deleteContact failed",
                    "",
                    "",
                    ""
                )
            );
        }

        IAddressBook.Contact[] memory contacts;
        (contacts, callError) = Caller.getAllContacts(deployed);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to getAllContacts failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(contacts.length, 2));
            testResult.assertResults.push(Assert.equal(contacts[0].id, 0));
            testResult.assertResults.push(
                Assert.equal(contacts[0].firstName, "Brian")
            );
            testResult.assertResults.push(Assert.equal(contacts[1].id, 2));
            testResult.assertResults.push(
                Assert.equal(contacts[1].lastName, "Doe")
            );
        }

        return testResult;
    }
}

contract TestOnlyOwner is ITest, Assert {
    NewSecondCaller secondCaller;

    constructor(address _secondCallerAddress) {
        secondCaller = NewSecondCaller(_secondCallerAddress);
    }

    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        IAddressBook deployed = IAddressBook(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Non-owner should revert on add/delete";

        uint[] memory phoneNumbers = new uint[](1);
        phoneNumbers[0] = 2021234567;
        (string memory message, bool callError) = secondCaller.addContact(
            deployed,
            "Brian",
            "Doyle",
            phoneNumbers
        );
        if (!callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Transaction failed to revert",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(
                Assert.equal(message, "Ownable: caller is not the owner")
            );
        }

        return testResult;
    }
}

contract NewUT is Cafe {
    constructor(
        address _secondCallerAddress
    ) ERC721("New Keyword Pin", "SCDN") {
        deployer = new TestDeployment();
        tests.push(new TestOnlyOwner(_secondCallerAddress));
    }
}

