        }
    }

    function callRemoteViewSalary(
        IEmployeeStorage _submission
    ) internal view returns (uint24, bool) {
        try _submission.viewSalary() returns (uint24 result) {
            return (result, false);
        } catch {
            return (0, true);
        }
    }

    function callRemoteViewShares(
        IEmployeeStorage _submission
    ) internal view returns (uint16, bool) {
        try _submission.viewShares() returns (uint16 result) {
            return (result, false);
        } catch {
            return (0, true);
        }
    }
}

contract testPublicStorage is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external view override returns (Cafe.TestResult memory) {
        IEmployeeStorage submission = IEmployeeStorage(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Constructor should initialize data correctly";

        // Shouldn't need to check for call failure for public automatic getters
        testResult.assertResults.push(Assert.equal(submission.name(), "Pat"));
        testResult.assertResults.push(
            Assert.equal(submission.idNumber(), 112358132134)
        );

        return testResult;
    }
}

contract testPrivateStorage is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        IEmployeeStorage submission = IEmployeeStorage(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult
            .message = "Private vars should revert, have public accessors";

        // Reset share numbers for test
        submission.debugResetShares();

        (uint16 shares, bool callError) = Caller.callRemoteShares(submission);
        testResult.assertResults.push(Assert.isTrue(callError));

        (shares, callError) = Caller.callRemoteViewShares(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to remoteViewShares failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(shares, 1000));
        }

        uint24 salary;
        (salary, callError) = Caller.callRemoteSalary(submission);
        testResult.assertResults.push(Assert.isTrue(callError));

        (salary, callError) = Caller.callRemoteViewSalary(submission);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to remoteViewSalary failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(salary, 50000));
        }
        return testResult;
    }
}

contract testPacking is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        IEmployeeStorage submission = IEmployeeStorage(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Variables should pack appropriately";
        (uint res, bool callError) = Caller.callRemoteCheckForPacking(
            submission,
            3
        );
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to checkForPacking failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(res, 0));
        }
        return testResult;
    }
}

contract testGrantShares is ITest, Assert {
    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        IEmployeeStorage submission = IEmployeeStorage(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Can grant shares, revert >= 5000";

        // Reset share numbers for test
        submission.debugResetShares();

        (string memory reason, bool callError) = Caller.callRemoteGrantShares(
            submission,
            5001
        );

        testResult.assertResults.push(Assert.isTrue(callError));
        testResult.assertResults.push(Assert.equal(reason, "Too many shares"));

        (reason, callError) = Caller.callRemoteGrantShares(submission, 1000);
        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to grantShares failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            uint16 shares;
            (shares, callError) = Caller.callRemoteViewShares(submission);
            testResult.assertResults.push(Assert.equal(shares, 2000));
        }
        return testResult;
    }
}

contract StorageUT is Cafe {
    constructor() ERC721("Storage Pin", "SCDS") {
        tests.push(new testPublicStorage());
        tests.push(new testPrivateStorage());
        tests.push(new testPacking());
        tests.push(new testGrantShares());
    }
}

