// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import "hardhat/console.sol";

import "./Cafe.sol";
import "./Assert.sol";

using List for List.ARList;

interface ISubmission {
    struct Car {
        string make;
        string model;
        string color;
        uint numberOfDoors;
    }

    function addCar(
        string calldata _make,
        string calldata _model,
        string calldata _color,
        uint _numberOfDoors
    ) external;

    function updateCar(
        uint _index,
        string calldata _make,
        string calldata _model,
        string calldata _color,
        uint _numberOfDoors
    ) external;

    function getUserCars(address _user) external view returns (Car[] memory);

    function getMyCars() external view returns (Car[] memory);

    function resetMyGarage() external;
}

library Caller {
    function addCar(
        ISubmission _submission,
        string memory _make,
        string memory _model,
        string memory _color,
        uint _numberOfDoors
    ) internal returns (bool) {
        try _submission.addCar(_make, _model, _color, _numberOfDoors) {
            return false;
        } catch {
            return true;
        }
    }

    function updateCar(
        ISubmission _submission,
        uint _index,
        string memory _make,
        string memory _model,
        string memory _color,
        uint _numberOfDoors
    ) internal returns (bool) {
        try
            _submission.updateCar(_index, _make, _model, _color, _numberOfDoors)
        {
            return false;
        } catch {
            return true;
        }
    }

    function getUserCars(
        ISubmission _submission,
        address _user
    ) internal view returns (ISubmission.Car[] memory, bool) {
        try _submission.getUserCars(_user) returns (
            ISubmission.Car[] memory result
        ) {
            return (result, false);
        } catch {
            ISubmission.Car[] memory badResult;
            return (badResult, true);
        }
    }

    function getMyCars(
        ISubmission _submission
    ) internal view returns (ISubmission.Car[] memory, bool) {
        try _submission.getMyCars() returns (ISubmission.Car[] memory result) {
            return (result, false);
        } catch {
            ISubmission.Car[] memory badResult;
            return (badResult, true);
        }
    }

    function resetMyGarage(ISubmission _submission) internal returns (bool) {
        try _submission.resetMyGarage() {
            return false;
        } catch {
            return true;
        }
    }
}

interface IStructsSecondCaller {
    function addCar(
        ISubmission _submission,
        string calldata _make,
        string calldata _model,
        string calldata _color,
        uint _numberOfDoors
    ) external;

    function secondUpdateCar(
        ISubmission _submission,
        uint _index,
        string calldata _make,
        string calldata _model,
        string calldata _color,
        uint _numberOfDoors
    ) external;

    function resetMyGarage(ISubmission _submission) external;
}

// Create a second contract to have a second address to test with
contract StructsSecondCaller {
    function addCar(
        ISubmission _submission,
        string calldata _make,
        string calldata _model,
        string calldata _color,
        uint _numberOfDoors
    ) public returns (bool) {
        return
            Caller.addCar(_submission, _make, _model, _color, _numberOfDoors);
    }

    function updateCar(
        ISubmission _submission,
        uint _index,
        string calldata _make,
        string calldata _model,
        string calldata _color,
        uint _numberOfDoors
    ) public returns (bool) {
        return
            Caller.updateCar(
                _submission,
                _index,
                _make,
                _model,
                _color,
                _numberOfDoors
            );
    }

    function resetMyGarage(ISubmission _submission) public returns (bool) {
        return Caller.resetMyGarage(_submission);
    }
}

contract TestAddAndUpdateCar is ITest, Assert {
    StructsSecondCaller secondCaller;

    constructor(address _secondCallerAddress) {
        secondCaller = StructsSecondCaller(_secondCallerAddress);
    }

    function execute(
        address _submissionAddress
    ) external override returns (Cafe.TestResult memory) {
        ISubmission submission = ISubmission(_submissionAddress);
        Cafe.TestResult memory testResult;
        testResult.assertResults.create();
        testResult.message = "Should allow saving cars for two users";

        // Reset garages for testing
        bool callError = Caller.resetMyGarage(submission);

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to resetMyGarage failed",
                    "",
                    "",
                    ""
                )
            );
        }

        callError = secondCaller.resetMyGarage(submission);

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to resetMyGarage failed",
                    "",
                    "",
                    ""
                )
            );
        }

        callError = Caller.addCar(submission, "Mazda", "Miata", "Silver", 2);

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to addCar failed", "", "", "")
            );
        }

        callError = Caller.addCar(submission, "Volkswagen", "GTI", "Red", 4);

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to addCar failed", "", "", "")
            );
        }

        callError = secondCaller.addCar(
            submission,
            "Ford",
            "Mustang",
            "Blue",
            2
        );

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to addCar failed", "", "", "")
            );
        }

        callError = secondCaller.addCar(
            submission,
            "Ford",
            "Focus",
            "Silver",
            4
        );

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(false, "Call to addCar failed", "", "", "")
            );
        }

        ISubmission.Car[] memory cars;
        (cars, callError) = Caller.getUserCars(submission, address(this));

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to getUserCars failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(cars[0].make, "Mazda"));
            testResult.assertResults.push(Assert.equal(cars[0].model, "Miata"));
            testResult.assertResults.push(
                Assert.equal(cars[0].color, "Silver")
            );
            testResult.assertResults.push(
                Assert.equal(cars[0].numberOfDoors, 2)
            );

            testResult.assertResults.push(
                Assert.equal(cars[1].make, "Volkswagen")
            );
            testResult.assertResults.push(Assert.equal(cars[1].model, "GTI"));
            testResult.assertResults.push(Assert.equal(cars[1].color, "Red"));
            testResult.assertResults.push(
                Assert.equal(cars[1].numberOfDoors, 4)
            );
        }

        (cars, callError) = Caller.getUserCars(
            submission,
            address(secondCaller)
        );

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to getUserCars failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(cars[0].make, "Ford"));
            testResult.assertResults.push(
                Assert.equal(cars[0].model, "Mustang")
            );
            testResult.assertResults.push(Assert.equal(cars[0].color, "Blue"));
            testResult.assertResults.push(
                Assert.equal(cars[0].numberOfDoors, 2)
            );

            testResult.assertResults.push(Assert.equal(cars[1].make, "Ford"));
            testResult.assertResults.push(Assert.equal(cars[1].model, "Focus"));
            testResult.assertResults.push(
                Assert.equal(cars[1].color, "Silver")
            );
            testResult.assertResults.push(
                Assert.equal(cars[1].numberOfDoors, 4)
            );
        }

        callError = Caller.updateCar(submission, 0, "Mazda", "Miata", "Red", 2);

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to updateCar failed",
                    "",
                    "",
                    ""
                )
            );
        }

        (cars, callError) = Caller.getUserCars(submission, address(this));

        if (callError) {
            testResult.assertResults.push(
                Assert.AssertResult(
                    false,
                    "Call to getUserCars failed",
                    "",
                    "",
                    ""
                )
            );
        } else {
            testResult.assertResults.push(Assert.equal(cars[0].color, "Red"));
        }

        return testResult;
    }
}

contract StructsUT is Cafe {
    constructor(address _secondCallerAddress) ERC721("Structs Pin", "SCDS") {
        tests.push(new TestAddAndUpdateCar(_secondCallerAddress));
    }
}

